import logo from './logo.svg';
import './App.css';

function App() {
  const form = document.querySelector('form');

  form.addEventListener('submit', async (event) => {
    event.preventDefault();

    const formData = new FormData(form);
    const response = await fetch('https://fp-services.ru/installment', {
      method: 'POST',
      body: formData
    });

    if (response.ok) {
      const userData = await response.json();
      // Сохраняем информацию о пользователе в localStorage или cookie
      localStorage.setItem('userData', JSON.stringify(userData));

      // Переходим на следующую страницу
      window.location.href = '/next-page.html'; // замените '/next-page.html' на URL нужной страницы
    } else {
      alert('Invalid login credentials');
    }
  });



}

export default App;
