        <table className='table'>
        <thead className='table_thead'>
            <tr>
                <th className='table_th'>Имя студента</th>
                <th className='table_th'>Дата начала</th>
                <th className='table_th'>Сумма</th>
                <th className='table_th'>Зачислен</th>
                <th className='table_th'>Отменен</th>
                <th className='table_th'>Платежи</th>
            </tr>
        </thead>

            <tbody>
                {
                    student.map((сource, index) => (
                        <tr key={index}>
                            <td className='table_td'>{сource.Name}</td>
                            <td className='table_td'>{getDateTime(data.CreatedAt)}</td>
                            <td className='table_td'>{сource.SumNeed} ₽</td>
                            <td className='table_td'>{getLocalizetBoolean(сource.IsEnrolled)}</td>
                            <td className='table_td'>{getLocalizetBoolean(сource.IsCancelled)}</td>
                            <td className='table_td'>{сource.Payments.length} платежа
                            <button type="button" onClick={showCreateModal}>Посмотреть</button>


                            </td>
                        </tr>

                    ))
                }
            </tbody>

    </table>
    )
}

export default payments;
