import './index.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import React, { useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import { Link } from 'react-router-dom';
import Login from './Login'; 




function Main() {

    const token = localStorage.getItem('token');

    if (token == '') {
        return MainTokenExeption()
    } else {
        console.log('MainPage:');

        return MainPage()
    }

}


function MainTokenExeption() {
    const navigate = useNavigate();

    function handleClick(event) {
        navigate('/');
    }

    return (
        <div>
            <div>Сеанс истек, требуется заново войти</div>

            <button
                className="button"
                type="button"
                onSubmit={handleClick}

            >Войти</button>

        </div>
    );
}

function MainPage() {
    var userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');
    const [rates, setRates] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false); // Состояние модального окна
    const [isCreateOpen, setIsCreateOpen] = useState(false); // Состояние модального окна

    const [showAlert, setShowAlert] = useState(false);
    const [errorModal, setErrorModal] = useState(
        {
            isOpen: false,
            errorText: ''
        });


    const navigate = useNavigate();

    function handleClick(event) {
        localStorage.clear();
        navigate('/');
        console.log('Токен после удаления:', localStorage.getItem('token'));
    }



    useEffect(() => {
        getRates();
    }, []);


    const [newRate, setNewRate] = useState({
        Name: '',
        ShortID: uniqueId(),
        Price: 0,
        Duration: '',
        SourceLink: "",
        Description: ''
    });

    const [isCreating, setIsCreating] = useState(false); // Флаг для отслеживания создания курса


    function handleClick(event) {
        navigate('/main');
    }

    // function uniqueId() {
    //     const dateString = Date.now().toString(36);
    //     const randomness = Math.random().toString(14)
    //     return dateString + randomness;
    // };


    function uniqueId(length) {
        const letters = 'abcdefghijklmnopqrstuvwxyz'; // набор букв, из которого будет производиться выбор
        let id = '';
        let le

        for (let i = 0; i < length; i++) {
          const randomIndex = Math.floor(Math.random() * letters.length);
          const randomLetter = letters.charAt(randomIndex);
          id += randomLetter;
        }

        return id;
      }



    //Получаем список курсво
    async function getRates() {
        try {
            const response = await fetch("https://fp-services.ru/installment/courses/get-list", {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${token}`
                }
            });

            if (response.ok) {
                const data = await response.json();

                console.log(data);

                setRates(data.Data);

            } else {
                const errorData = await response.json();
                console.error('Ошибка авторизации:', errorData);

                setErrorModal(
                    {
                        isOpen: true,
                        errorText: errorData
                    }

                )

            }
        } catch (error) {
            console.error('Ошибка:', error);
        }



    }

    async function createRate() {

        try {
            const response = await fetch("https://fp-services.ru/installment/courses/create", {
                method: "POST",
                headers: {
                    "Authorization": `Bearer ${token}`,
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(newRate) // Отправляем данные нового курса
            });

            if (response.ok) {
                const data = await response.json();

                console.log(data);

                // Обновляем список курсов после успешного создания
                getRates();
            } else {
                const errorData = await response.json();
                console.error('Ошибка создания курса:', errorData);
            }
        } catch (error) {
            console.error('Ошибка:', error);
        }
    }

    function closeModal() {
        setIsModalOpen(false);
    }

    function handleCreateClick() {
        setIsModalOpen(true);
    }

    function showCreateModal() {
        setIsCreateOpen(true)
    }

    // function hideCreateModal() {
    //     setIsCreateOpen(false)
    // }

    //   function handleCreateClick() {

    //     window.location.reload();
    //   }

    function handleSaveClick() {
        createRate();
        setIsModalOpen(false);
        setNewRate({
            Name: '',
            ShortID: uniqueId(),
            Price: 0,
            Duration: '',
            Description: ''
        });
    }

    function handleCancelClick() {
        setIsModalOpen(false);
        setNewRate({
            Name: '',
            ShortID: uniqueId(),
            Price: 0,
            Duration: '',
            Description: ''
        });
    }

    //   function handleCreateClick() {
    //     setIsCreating(true);
    //     openModal();
    //   }

    function handleInputChange(event) {
        const { name, value } = event.target;
        console.log(typeof name, typeof value);
        setNewRate((prevRate) => ({
            ...prevRate,
            [name]: value
        }));
    }

    function handlePriceInputChange(event) {
        const { name, value } = event.target;
        console.log(typeof name, typeof value);

        setNewRate((prevRate) => ({
            ...prevRate,
            [name]: parseFloat(value)
        }));
    }

    //Обновление страницы при нажити на кнопку "Повторить в модалке"
    const handleClose = () => {
        window.location.reload();
    };

    //Создаем список курсов
    return (
        <div className='main'>

            {/* <Create myBoolean={isCreateOpen} handleClick={hideCreateModal} /> */}

            {/* Шапка сайта */}
            <div className='header'>
                <div className='header-title'>
                    <div className='logo'>Логотип</div>
                    <h3 className='header-text'>SHOOKRU EDUCATION</h3>
                </div>

                <div className='header-inner'>
                    <h5 className='Name'>{userName}</h5>
                    <button className='header-button' type="button" onClick={handleClick}>Выйти</button>
                </div>
            </div>

            {/* Кнопка после которой вызыается модалка курсов */}
            <div className="button-table">
                {!isCreating && (
                    <button type="button" onClick={handleCreateClick}>Создать</button>
                )}
            </div>

            {/* Таблица */}
            <table className='table'>
                <thead className='table_thead'>
                    <tr>
                        <th className='table_th'>Имя</th>
                        <th className='table_th'>Цена</th>
                        <th className='table_th'>Продолжительность</th>
                        <th className='table_th'>Описание</th>
                    </tr>
                </thead>

                {rates.map((rate, index) => (
                    <tr key={index}>
                        <td className='table_td'>{rate.Name}</td>
                        <td className='table_td'>{rate.Price}₽</td>
                        <td className='table_td'>{rate.Duration}</td>
                        <td className='table_td'>{rate.Description}</td>
                    </tr>

                ))}

            </table>     {/* конец таблицы */}

            {/* Модалка для создания курсов */}
            {isModalOpen && (
                <div className="modal">
                    <button className='modal-cancellation' onClick={handleCancelClick}>

                        <link rel="stylesheet" href="https://fonts.googleapis.com/css2?family=Material+Symbols+Outlined:opsz,wght,FILL,GRAD@48,700,0,0" />


                    </button>
                    <div className="create">
                        <input
                            type="text"
                            name="Name"
                            value={newRate.Name}
                            onChange={handleInputChange}
                            placeholder="Название"
                        />
                        <input
                            type="number"
                            name="Price"
                            value={newRate.Price}
                            onChange={handlePriceInputChange}
                            placeholder="Цена"
                        />
                        <input
                            type="text"
                            name="Duration"
                            value={newRate.Duration}
                            onChange={handleInputChange}
                            placeholder="Продолжительность"
                        />
                        <input className='create'
                            type="text"
                            name="SourceLink"
                            value={newRate.SourceLink}
                            onChange={handleInputChange}
                            placeholder="Cсылка на курс"
                        />
                        <input className='create-description'
                            type="text"
                            name="Description"
                            value={newRate.Description}
                            onChange={handleInputChange}
                            placeholder="Описание"
                        />

                        <div className='create-button'>
                            <button onClick={handleSaveClick}>Создать</button>

                        </div>
                    </div>
                    <div class="overlay"> </div>
                </div>


            )}

            <div>
                <Modal isOpen={errorModal.isOpen} onRequestClose={handleClose} className="modal">
                    <h3 className='modal-error'> Ошибка</h3>
                    <p className='modal-text'>{errorModal.errorText}</p>
                    <button className='modal-button' onClick={handleClose}>Повторить</button>
                </Modal>
            </div>
        </div>
    );

}

export default Main;
