import './index.css';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import React, { useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import { Link } from 'react-router-dom';
import Create from './create';
import Back from './Back.png'
import Main from './main';
import { useParams } from 'react-router-dom';

function CurseDetails() {


    var userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');
    var userName = localStorage.getItem('userName');
    const navigate = useNavigate();
    const [rates, setRates] = useState([]);
    const { courseUID } = useParams();
    const [errorModal, setErrorModal] = useState(
        {
            isOpen: false,
            errorText: ''
        });

    function handleGoBack() {
        navigate('/main'); // Переход на страницу "main"
      }

      function handleClick(event) {
        navigate('/');
    }

    useEffect(() => {
        getRates();
    }, []);

// Присваиваем значение `CourseUID` переменной

    //   fetch(`https://fp-services.ru/courses/get?course_uid=${courseUID}`, {
    //     method: "GET",
    //     headers: {
    //       "Authorization": `Bearer ${token}`
    //     }
    //   })
    //     .then((response) => response.json())
    //     .then((data) => {
    //       // Обработка полученных данных
    //       console.log(data);
    //     })
    //     .catch((error) => {
    //       // Обработка ошибок
    //       console.error(error);
    //     });


    async function getRates() {
        console.log()

        try {
            const response = await fetch(`https://fp-services.ru/courses/get?course_uid=${courseUID}`, {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${token}`
                }
            });

            if (response.ok) {
                const data = await response.json();

                console.log(data);



            } else {
                const errorData = await response.json();
                console.error('Ошибка авторизации:', errorData);

                setErrorModal(
                    {
                        isOpen: true,
                        errorText: errorData
                    }

                )
            }
        } catch (error) {
            console.error('Ошибка:', error);
        }

    }



    return (
        <div className='main'>
             {/* Шапка сайта */}
             <div className='header'>
                <div className='header-title'>
                    <div className='logo'></div>
                    <h3 className='header-text'>SHOOKRU EDUCATION</h3>
                    <button className='button-back' onClick={handleGoBack}> Назад</button>
                </div>

                <div className='header-inner'>
                    <h5 className='Name'>{userName}</h5>
                    <button className='header-button' type="button" onClick={handleClick}>Выйти</button>
                </div>
            </div>

        </div>
        );
}

export default CurseDetails;
