import './index.css';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import React, { useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import { Link } from 'react-router-dom';
import Create from './create';
import Back from './Back.png'
import Main from './main';
import { useParams } from 'react-router-dom';




function Token() {

    const token = localStorage.getItem('token');

    if (token == '') {
        return MainTokenExeption()
    } else {
        console.log('MainPage:');

        return MainPage()
    }

}

function CurseDetails() {





    var userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');
    var userName = localStorage.getItem('userName');
    const navigate = useNavigate();
    const [rates, setRates] = useState([]);
    const { courseUID } = useParams();
    const [errorModal, setErrorModal] = useState(
        {
            isOpen: false,
            errorText: ''
        });

        const [newCource, setNewCource] = useState({
            Name: '',
            ShortID: uniqueId(),
            Price: 0,
            Duration: '',
            SourceLink: "",

            Description: '',
            detailsButton: true, // флаг указывает на наличие кнопки "Подробнее"
          });

          function uniqueId() {
            const letters = 'abcdefghijklmnopqrstuvwxyz'; // набор букв, из которого будет производиться выбор
            let id = '';
            let length = 5;

            for (let i = 0; i < length; i++) {
              const randomIndex = Math.floor(Math.random() * letters.length);
              const randomLetter = letters.charAt(randomIndex);
              id += randomLetter;
            }

            return id;
          }

    function handleGoBack() {
        navigate('/main'); // Переход на страницу "main"
      }

      function handleClick(event) {
        navigate('/');
    }

    useEffect(() => {
        getRates();
    }, []);

    async function getRates() {
        console.log(courseUID);

        try {
            const response = await fetch(`https://fp-services.ru/installment/courses/get?course_uid=${courseUID}`, {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${token}`
                },
                body: JSON.stringify(newCource)
            });

            if (response.ok) {
                const data = await response.json();

                console.log(data);

                  setRates(data.Data);

            } else {
                const errorData = await response.json();
                console.error('CurseDetails error', errorData);

                setErrorModal(
                    {
                        isOpen: true,
                        errorText: errorData
                    }

                )
            }
        } catch (error) {
            console.error('Ошибка:', error);
        }

    }

    return (
        <div className='main'>
             {/* Шапка сайта */}
             <div className='header'>
                <div className='header-title'>
                    <div className='logo'></div>
                    <h3 className='header-text'>SHOOKRU EDUCATION</h3>
                    <button className='button-back' onClick={handleGoBack}> Назад</button>
                </div>

                <div className='header-inner'>
                    <h5 className='Name'>{userName}</h5>
                    <button className='header-button' type="button" onClick={handleClick}>Выйти</button>
                </div>
            </div>


            <table className='table'>
                <thead className='table_thead'>
                    <tr>
                        <th className='table_th'>Имя студента</th>
                        <th className='table_th'>Дата начала</th>
                        <th className='table_th'>Продолжительность</th>
                        <th className='table_th'>SourceLink</th>
                        <th className='table_th'>InstallmentLink</th>
                        <th className='table_th'>CreatedAt</th>
                        <th className='table_th'>Students</th>
                    </tr>
                </thead>

                {rates.map((сource, index) => (
                    <tr key={index}>
                        <td className='table_td'>{сource.Name}</td>
                        <td className='table_td'>{сource.Price} ₽</td>
                        <td className='table_td'>{сource.Duration}</td>
                        <td className='table_td'>{сource.Description}</td>

                    </tr> 

                ))}

            </table>  

        </div>
        );
}

export default CurseDetails;
