import './index.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import React, { useEffect } from 'react';
import axios from 'axios';

function Main() {
    console.log('Main:');

    const userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');

    if (token == '') {
        return MainTokenExeption()
    } else {
        console.log('MainPage:');

        return MainPage()
    }

}


function MainTokenExeption() {
    const navigate = useNavigate();

    function handleClick(event) {
        navigate('/');
    }

    return (
        <div>
            <div>Сеанс истек, требуется заново войти</div>

            <button
                className="button"
                type="button"
                onSubmit={handleClick}

            >Войти</button>

        </div>
    );
}

function MainPage() {
    const [userName, setUserName] = localStorage.getItem('userName');
    const token = localStorage.getItem('token');
    const [rates, setRates] = useState([]);

    const navigate = useNavigate();

    function handleClick(event) {
        localStorage.clear();
        navigate('/');
        console.log('Токен после удаления:', localStorage.getItem('token'));
    }

    useEffect(() => {
        getRates();
    }, []);

    async function getRates() {
        try {
            const response = await fetch("https://fp-services.ru/installment/courses/get-list", {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${token}`
                }


            });

            if (response.ok) {
                const data = await response.json();

                console.log(data);

                setRates(data.Data);
            } else {
                const errorData = await response.json();
                console.error('Ошибка авторизации:', errorData);
            }
        } catch (error) {
            console.error('Ошибка:', error);
        }


    }

    const sortData = (column) => {
        if (sortColumn === column) {
          setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
        } else {
          setSortColumn(column);
          setSortDirection('asc');
        }
      };

      const sortedRates = rates.sort((a, b) => {
        const aValue = a[sortColumn];
        const bValue = b[sortColumn];

        if (sortDirection === 'asc') {
          return aValue.localeCompare(bValue);
        } else {
          return bValue.localeCompare(aValue);
        }
      });

    return (
        <div className='main'>
            {/* <ul className='table'>
                {rates.map((rate, index) => (
                    <li className='table_title' key={index}>
                        <p className='table_iner'> <strong>Name:</strong> {rate.Name}</p>
                        <p className='table_iner'> <strong>Price:</strong>{rate.Price}</p>
                        <p className='table_iner'><strong> Duration: </strong>{rate.Duration}</p>
                        <p className='table_iner'><strong> Description: </strong>{rate.Description}</p>
                    </li>
                ))}
            </ul> */}
            <table className='table'>
                <thead>
                    <tr>
                        <th onClick={() => sortData('Name')}>Name</th>
                        <th onClick={() => sortData('Price')}>Price</th>
                        <th onClick={() => sortData('Duration')}>Duration</th>
                        <th onClick={() => sortData('Description')}>Description</th>
                    </tr>
                </thead>
                <tbody>
                    {sortedRates.map((rate, index) => (
                        <tr key={index}>
                            <td>{rate.Name}</td>
                            <td>{rate.Price}</td>
                            <td>{rate.Duration}</td>
                            <td>{rate.Description}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="button">
                <button type="button" onClick={handleClick}>Выйти</button>
            </div>
        </div>

    );

}

export default Main;
