import './index.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import React, { useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';



function Main() {
    console.log('Main:');

    const userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');

    if (token == '') {
        return MainTokenExeption()
    } else {
        console.log('MainPage:');

        return MainPage()
    }

}


function MainTokenExeption() {
    const navigate = useNavigate();

    function handleClick(event) {
        navigate('/');
    }

    return (
        <div>
            <div>Сеанс истек, требуется заново войти</div>

            <button
                className="button"
                type="button"
                onSubmit={handleClick}

            >Войти</button>

        </div>
    );
}

function MainPage() {
    const [userName, setUserName] = localStorage.getItem('userName');
    const token = localStorage.getItem('token');
    const [rates, setRates] = useState([]);
    const [isOpen, setIsOpen] = React.useState(false);
    const [errorModal, setErrorModal] = useState(
        {
            isOpen: false,
            errorText: ''
        });

        function YourComponent() {
            const [name, setName] = useState('');
            const [price, setPrice] = useState('');
            const [duration, setDuration] = useState('');
            const [description, setDescription] = useState('');
            const [isCreating, setIsCreating] = useState(false);

            const handleInputChange = (e) => {
                // Обновляем значение полей ввода
                setName(e.target.value);
                setPrice(e.target.value);
                setDuration(e.target.value);
                setDescription(e.target.value);
              };

              const handleCreate = async () => {
                try {
                  setIsCreating(true);
                  const response = await axios.post("https://fp-services.ru/installment", {
                    Name: name,
                    Price: parseFloat(price),
                    Duration: duration,
                    Description: description
                  }, {
                    headers: {
                      "Authorization": `Bearer {{token}}`,
                      "Content-Type": "application/json"
                    }
                  });

                  if (response.data.Data === "OK") {
                    // Успешно создано
                    console.log('Курс успешно создан');
                    // Можете выполнить дополнительные действия, например, обновить список курсов
                    // или перенаправить пользователя на другую страницу
                  } else {
                    // Обработка ошибки создания курса
                    console.error('Ошибка при создании курса:', response.data.Error);
                  }

                  // Очистка полей ввода
                  setName('');
                  setPrice('');
                  setDuration('');
                  setDescription('');
                } catch (error) {
                  console.error('Ошибка при создании курса:', error);
                } finally {
                  setIsCreating(false);
                }
              };
                }


    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);

    const [showAlert, setShowAlert] = useState(false);

    const handleButtonClick = () => {
        setShowAlert(true);
    };

    const handleCloseClick = () => {
        setShowAlert(false);
    };

    const navigate = useNavigate();

    function handleClick(event) {
        localStorage.clear();
        navigate('/');
        console.log('Токен после удаления:', localStorage.getItem('token'));
    }



    useEffect(() => {
        getRates();
    }, []);

//Получаем список курсво
    async function getRates() {
        try {
            const response = await fetch("https://fp-services.ru/installment/courses/get-list", {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${token}`
                }


            });

            if (response.ok) {
                const data = await response.json();

                console.log(data);

                setRates(data.Data);

            } else {
                const errorData = await response.json();
                console.error('Ошибка авторизации:', errorData);

                setErrorModal(
                    {
                        isOpen: true,
                        errorText: errorData
                    }

                )

            }
        } catch (error) {
            console.error('Ошибка:', error);
        }



    }



    // function handleCreateCourse() {
    //     // Создайте пустой объект курса
    //     const emptyCourse = {
    //         name: '',
    //         price: '',
    //         duration: '',
    //         description: '',
    //     };
    //     // Добавьте пустой объект в список курсов
    //     setRates([...rates, emptyCourse]);
    // }

    // function handleSaveCourse(course) {
    //     // Отправьте запрос на сохранение курса
    //     createCourse(course);
    //     // Удалите пустой объект курса из списка курсов
    //     setRates(rates.filter((rate) => rate !== course));
    // }


    //Создаем курсы 




//Обновление страницы при нажити на кнопку "Повторить в модалке"
    const handleClose = () => {
        window.location.reload();
    };



    //Создаем список курсов
    return (
        <div className='main'>
            <table className='table'>
                <thead className='table_thead'>
                    <tr>
                        <th className='table_th'>Name</th>
                        <th className='table_th'>Price</th>
                        <th className='table_th'>Duration</th>
                        <th className='table_th'>Description</th>
                    </tr>
                </thead>
                <tbody className='table_tbody'>
                    {rates.map((rate, index) => (
                        <tr key={index}>
                            <td className='table_td'>{rate.Name}</td>
                            <td className='table_td'>{rate.Price}₽</td>
                            <td className='table_td'>{rate.Duration}</td>
                            <td className='table_td'>{rate.Description}</td>
                        </tr>

                    ))}

                    {/* Пустая строка для создания нового курса */}


                </tbody>

            </table>

            <div>
      <input type="text" value={name} onChange={handleInputChange} placeholder="Название" />
      <input type="text" value={price} onChange={handleInputChange} placeholder="Цена" />
      <input type="text" value={duration} onChange={handleInputChange} placeholder="Длительность" />
      <input type="text" value={description} onChange={handleInputChange} placeholder="Описание" />
      {price && price && duration && description && (
        <button onClick={handleCreate} disabled={isCreating}>
          {isCreating ? 'Создание...' : 'Сохранить'}
        </button>
      )}
    </div>

            <div className="button">
                <button type="button" onClick={handleClick}>Выйти</button>
            </div>




            <div>

                <Modal isOpen={errorModal.isOpen} onRequestClose={handleClose} className="modal">
                    <h3 className='modal-error'> Ошибка</h3>
                    <p className='modal-text'>{errorModal.errorText}</p>
                    <button className='modal-button' onClick={handleClose}>Повторить</button>
                </Modal>
            </div>


        </div>

    );

}

export default Main;
