import './index.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import React, { useEffect } from 'react';
import axios from 'axios';
function Main() {
    console.log('Main:');

    const userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');

    if (token == '') {
        return MainTokenExeption()
    } else {
        console.log('MainPage:');

        return MainPage()
    }

}


function MainTokenExeption() {
    const navigate = useNavigate();

    function handleClick(event) {
        navigate('/');
    }

    return (
        <div>
            <div>Сеанс истек, требуется заново войти</div>

            <button
                className="button"
                type="button"
                onSubmit={handleClick}

            >Войти</button>

        </div>
    );
}

function MainPage() {
    const [userName, setUserName] = localStorage.getItem('userName');
    const token = localStorage.getItem('token');

    const navigate = useNavigate();

    function handleClick(event) {
        localStorage.clear();
        navigate('/');
        console.log('Токен после удаления:', localStorage.getItem('token'));
    }

    useEffect(() => {
        async function getRates() {
            try {
                const response = await fetch("https://fp-services.ru/installment/courses/get-list", {
                    method: "GET",
                    headers: {
                        "Authorization": `Bearer ${token}`
                    }

                });

                if (response.ok) {
                    const data = await response.json();

                    console.log(data);
                } else {
                    const errorData = await response.json();
                    console.error('Ошибка авторизации:', errorData);
                }
            } catch (error) {
                console.error('Ошибка:', error);
            }
        }

        getRates();
    }, []);

    return (
        <div>
            <div>userName</div>

            <button className="button" type="button" onClick={handleClick}>Выйти</button>


        </div>
    );

}

export default Main;
