import './index.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Main() {
    const userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');

    if (token == '') {
        return MainTokenExeption()
    } else {
        return MainPage()
    }

    const [courses, setCourses] = useState([]);

useEffect(() => {
  const fetchCourses = async () => {
    try {
      const token = 'your_token_here'; // Замените на свой токен
      const response = await fetch('{{host}}/courses/get-list', {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setCourses(data.Data);
      } else {
        throw new Error('Ошибка получения списка курсов');
      }
    } catch (error) {
      console.error('Произошла ошибка:', error);
    }
  };

  fetchCourses();
}, []);

return (
  <ul>
    {courses.map((course) => (
      <li key={course.CourseUID}>
        <h3>{course.Name}</h3>
        <p>Цена: {course.Price}</p>
        <p>Продолжительность: {course.Duration}</p>
        <p>{course.Description}</p>
      </li>
    ))}
  </ul>
);
}

export default Main;

function MainTokenExeption() {
    const navigate = useNavigate();

    function handleClick(event) {
        navigate('/');
    }

    return (
        <div>
            <div>Сеанс истек, требуется заново войти</div>

            <button 
                className="button"
                type="button"
                onSubmit={handleClick}

            >Войти</button>

      </div>
      );
}

function MainPage() {
    const [userName, setUserName] = localStorage.getItem('userName');

    const navigate = useNavigate();

    function handleClick(event) {
        localStorage.clear();
        navigate('/');
        console.log('Токен после удаления:', localStorage.getItem('token'));
    }


      return (
        <div>
            <div>userName</div>

            <button className="button" type="button" onClick={handleClick }>Выйти</button>


      </div>
      );
}
