import './index.css';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

import React, { useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import { token, getRates } from './main';
import userName from './Login';


function Create({ myBoolean, handleClick }) {

  const [newRate, setNewRate] = useState({
    Name: '',
    ShortID: uniqueId(),
    Price: 0,
    Duration: '',
    SourceLink: "",
    Description: ''
  });

  function uniqueId() {
    const dateString = Date.now().toString(36);
    const randomness = Math.random().toString(36)
    return dateString + randomness;
  };

  function handleSaveClick() {

  }

  function handleInputChange(event) {
    const { name, value } = event.target;
    console.log(typeof name, typeof value);
    setNewRate((prevRate) => ({
        ...prevRate,
        [name]: value
    }));
}

function handlePriceInputChange(event) {
    const { name, value } = event.target;
    console.log(typeof name, typeof value);

    setNewRate((prevRate) => ({
        ...prevRate,
        [name]: value
    }));
}

  return (
    <div>
      {myBoolean && (
        <div className="modal">
          <button className='modal-cancellation' onClick={handleClick}>

            <link rel="stylesheet" href="https://fonts.googleapis.com/css2?family=Material+Symbols+Outlined:opsz,wght,FILL,GRAD@48,700,0,0" />


          </button>
          <div className="create">
            <input
              type="text"
              name="Name"
              value={newRate.Name}
              // onChange={handleInputChange}
              placeholder="Название"
            />
            <input
              type="number"
              name="Price"
              value={newRate.Price}
              // onChange={handlePriceInputChange}
              placeholder="Цена"
            />
            <input
              type="text"
              name="Duration"
              value={newRate.Duration}
              // onChange={handleInputChange}
              placeholder="Продолжительность"
            />
            <input className='create'
              type="text"
              name="SourceLink"
              value={newRate.SourceLink}
              // onChange={handleInputChange}
              placeholder="Cсылка"
            />
            <input className='create-description'
              type="text"
              name="Description"
              value={newRate.Description}
              // onChange={handleInputChange}
              placeholder="Описание"
            />


            <div className='create-button'>
              <button onClick={handleSaveClick}>Создать</button>

            </div>
          </div>
          <div class="overlay"> </div>
        </div>



      )}
    </div>
  );
}


export default Create;
