import './index.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import React, { useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';



function Main() {
    console.log('Main:');

    const userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');

    if (token == '') {
        return MainTokenExeption()
    } else {
        console.log('MainPage:');

        return MainPage()
    }

}


function MainTokenExeption() {
    const navigate = useNavigate();

    function handleClick(event) {
        navigate('/');
    }

    return (
        <div>
            <div>Сеанс истек, требуется заново войти</div>

            <button
                className="button"
                type="button"
                onSubmit={handleClick}

            >Войти</button>

        </div>
    );
}

function MainPage() {
    const [userName, setUserName] = localStorage.getItem('userName');
    const token = localStorage.getItem('token');
    const [rates, setRates] = useState([]);
    const [showAlert, setShowAlert] = useState(false);
    const [errorModal, setErrorModal] = useState(
        {
            isOpen: false,
            errorText: ''
        });
    const [newCourse, setNewCourse] = useState({
        name: '',
        price: '',
        duration: '',
        description: '',
    });





    const handleButtonClick = () => {
        setShowAlert(true);
    };

    const handleCloseClick = () => {
        setShowAlert(false);
    };

    const navigate = useNavigate();

    function handleClick(event) {
        localStorage.clear();
        navigate('/');
        console.log('Токен после удаления:', localStorage.getItem('token'));
    }



    useEffect(() => {
        getRates();
    }, []);

//Получаем список курсво
    async function getRates() {
        try {
            const response = await fetch("https://fp-services.ru/installment/courses/get-list", {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${token}`
                }


            });

            if (response.ok) {
                const data = await response.json();

                console.log(data);

                setRates(data.Data);

            } else {
                const errorData = await response.json();
                console.error('Ошибка авторизации:', errorData);

                setErrorModal(
                    {
                        isOpen: true,
                        errorText: errorData
                    }

                )

            }
        } catch (error) {
            console.error('Ошибка:', error);
        }



    }



    function handleCreateCourse() {
        // Создайте пустой объект курса
        const emptyCourse = {
            name: '',
            price: '',
            duration: '',
            description: '',
        };
        // Добавьте пустой объект в список курсов
        setRates([...rates, emptyCourse]);
    }

    function handleSaveCourse(course) {
        // Отправьте запрос на сохранение курса
        createCourse(course);
        // Удалите пустой объект курса из списка курсов
        setRates(rates.filter((rate) => rate !== course));
    }


    //Создаем курсы 
        async function createCourse() {
        try {
            const response = await fetch('https://fp-services.ru/installment/courses/create', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify(newCourse),
            });

            if (response.ok) {
                // Курс успешно создан
                console.log('Курс успешно создан');
                // Выполните здесь дополнительные действия, если необходимо
            } else {
                const errorData = await response.json();
                console.error('Ошибка при создании курса:', errorData);
            }
        } catch (error) {
            console.error('Ошибка:', error);
        }
    }



//Обновление страницы при нажити на кнопку "Повторить в модалке"
    const handleClose = () => {
        window.location.reload();
    };



    //Создаем список курсов
    return (
        <div className='main'>
            <table className='table'>
                <thead className='table_thead'>
                    <tr>
                        <th className='table_th'>Name</th>
                        <th className='table_th'>Price</th>
                        <th className='table_th'>Duration</th>
                        <th className='table_th'>Description</th>
                    </tr>
                </thead>
                <tbody className='table_tbody'>
                    {rates.map((rate, index) => (
                        <tr key={index}>
                            <td className='table_td'>{rate.Name}</td>
                            <td className='table_td'>{rate.Price}₽</td>
                            <td className='table_td'>{rate.Duration}</td>
                            <td className='table_td'>{rate.Description}</td>
                            <td className="table_td">
                                {/* Кнопка "Сохранить" для сохранения изменений */}
                                <button type="button" onClick={() => handleSaveCourse(rate)}>
                                    Сохранить
                                </button>
                            </td>
                        </tr>
                    ))}

                    {/* Пустая строка для создания нового курса */}
                    <tr>
                        <td className="table_td">
                            <input
                                type="text"
                                value={newCourse.name}
                                onChange={(e) => setNewCourse({ ...newCourse, name: e.target.value })}
                            />
                        </td>
                        <td className="table_td">
                            <input
                                type="number"
                                value={newCourse.price}
                                onChange={(e) => setNewCourse({ ...newCourse, price: e.target.value })}
                            />
                        </td>
                        <td className="table_td">
                            <input
                                type="text"
                                value={newCourse.duration}
                                onChange={(e) => setNewCourse({ ...newCourse, duration: e.target.value })}
                            />
                        </td>
                        <td className="table_td">
                            <input
                                type="text"
                                value={newCourse.description}
                                onChange={(e) => setNewCourse({ ...newCourse, description: e.target.value })}
                            />
                        </td>
                        <td className="table_td">
                            {/* Кнопка "Создать" для создания нового курса */}
                            <button type="button" onClick={handleCreateCourse}>
                                Создать
                            </button>
                        </td>
                    </tr>
                </tbody>
            </table>

            <div className="button">
                <button type="button" onClick={handleClick}>Выйти</button>
            </div>




            <div>

                <Modal isOpen={errorModal.isOpen} onRequestClose={handleClose} className="modal">
                    <h3 className='modal-error'> Ошибка</h3>
                    <p className='modal-text'>{errorModal.errorText}</p>
                    <button className='modal-button' onClick={handleClose}>Повторить</button>
                </Modal>
            </div>


        </div>

    );

}

export default Main;
