import './index.css';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Payments from './payments';

function CurseDetails() {
    var userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');
    const { courseUID } = useParams();
    const navigate = useNavigate();
    const [openingCreateModal, setStateForCreateModal] = useState(false);
    const [data, setData] = useState();
    const [student, setStudent] = useState([]);
    const [error, setError] = useState(
        {
            isOpen: false,
            errorText: ''
        });
    const [paymentsData, setPaymentData] = useState(
        {
            isOpen: false,
            data: {}
        });

    function showCreateModal() {
        setStateForCreateModal(true)
    }

    function hideCreateModal() {
        setStateForCreateModal(false)
    }

    function createModalResultSucces() {
        hideCreateModal();
        getRates();

    }

    function showPaymentsModal() {
        setPaymentData(
            {
                isOpen: true,
                data: student.Payments
            }
        )
    }

    function hidePaymentsModal() {
        setPaymentData(
            {
                isOpen: false,
                data: {}
            }
        )
    }


    function handleGoBack() {
        navigate('/main'); // Переход на страницу "main"
    }

    function handleClick(event) {
        navigate('/');
    }

    useEffect(() => {
        getRates();
    }, []);

    function getDateTime(dateString) {
        const formattedDate = new Date(dateString).toLocaleString();
        return formattedDate
    }

    function getLocalizetBoolean(bool) {
        if (bool == true) {
            return "Da"
        } else {
            return "Net"
        }
    }

    async function getRates() {
        console.log(courseUID);

        try {
            const response = await fetch(`https://fp-services.ru/installment/courses/get?course_uid=${courseUID}`, {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${token}`
                }
            });
            if (response.ok) {
                const responseData = await response.json();

                console.log(responseData.Data);

                setData(responseData.Data);

                setStudent(responseData.Data.Students)


                console.log(data);
                console.log(student);
            } else {
                const errorData = await response.json();
                console.error('CurseDetails error', errorData);
                setError({
                    isOpen: true,
                    errorText: errorData,
                });
            }
        } catch (error) {
            console.error('Ошибка:', error);
        }

    };


    return (
        <div className='main'>
            {/* Шапка сайта */}
            <div className='header'>
                <div className='header-title'>
                    <div className='logo'></div>
                    <h3 className='header-text'>SHOOKRU EDUCATION</h3>
                    <button className='button-back' onClick={handleGoBack}> Назад</button>
                </div>

                <div className='header-inner'>
                    <h5 className='Name'>{userName}</h5>
                    <button className='header-button' type="button" onClick={handleClick}>Выйти</button>
                </div>
            </div>

            <Payments paymentsData={paymentsData.isOpen} cancel={hide}/>
            <p>Название</p>
            <p>{data.Name}</p>

            <p>Цена</p>
            <p>{data.Price} ₽</p>

            <p>Длительность</p>
            <p>{data.Duration}</p>

            <p>Описание</p>
            <p>{data.Description}</p>

            <p>Ссылка на курс</p>
            <p>{data.SourceLink}</p>

            <p>Ссылка на рассрочку по курсу</p>
            <p>{data.InstallmentLink}</p>

            <p>Создан</p>
            <p>{getDateTime(data.CreatedAt)}</p>



            <table className='table'>
                <thead className='table_thead'>
                    <tr>
                        <th className='table_th'>Имя студента</th>
                        <th className='table_th'>Дата начала</th>
                        <th className='table_th'>Сумма</th>
                        <th className='table_th'>Зачислен</th>
                        <th className='table_th'>Отменен</th>
                        <th className='table_th'>Платежи</th>
                    </tr>
                </thead>

                {student !== null && (
                    <tbody>
                        {
                            student.map((сource, index) => (
                                <tr key={index}>
                                    <td className='table_td'>{сource.Name}</td>
                                    <td className='table_td'>{getDateTime(data.CreatedAt)}</td>
                                    <td className='table_td'>{сource.SumNeed} ₽</td>
                                    <td className='table_td'>{getLocalizetBoolean(сource.IsEnrolled)}</td>
                                    <td className='table_td'>{getLocalizetBoolean(сource.IsCancelled)}</td>
                                    <td className='table_td'>{сource.Payments.length} платежа
                                    <button type="button" onClick={showPaymentsModal}>Посмотреть</button>


                                    </td>
                                </tr>

                            ))
                        }
                    </tbody>
                )}

            </table>

        </div>
    );
}

export default CurseDetails;
