import './index.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Main() {
    const userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');

    if (token == '') {
        return MainTokenExeption()
    } else {
        return MainPage()
    }
    function ListComponent() {
        const [dataList, setDataList] = useState([]);

        useEffect(() => {
          const fetchData = async () => {
            try {
              const response = await fetch('https://fp-services.ru/installment/courses/get-lis');
              if (response.ok) {
                const data = await response.json();
                setDataList(data);
              } else {
                throw new Error('Ошибка получения данных');
              }
            } catch (error) {
              console.error('Произошла ошибка:', error);
            }
          };

          fetchData();
        }, []);

        return (
          <ul>
            {dataList.map((item) => (
              <li key={item.id}>{item.name}</li>
            ))}
          </ul>
        );
      }

}

export default Main;

function MainTokenExeption() {
    const navigate = useNavigate();

    function handleClick(event) {
        navigate('/');
    }

    return (
        <div>
            <div>Сеанс истек, требуется заново войти</div>

            <button 
                className="button"
                type="button"
                onSubmit={handleClick}

            >Войти</button>

      </div>
      );
}

function MainPage() {
    const [userName, setUserName] = localStorage.getItem('userName');

    const navigate = useNavigate();

    function handleClick(event) {
        localStorage.clear();
        navigate('/');
        console.log('Токен после удаления:', localStorage.getItem('token'));
    }


      return (
        <div>
            <div>userName</div>

            <button className="button" type="button" onClick={handleClick }>Выйти</button>


      </div>
      );
}
