import './index.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import React, { useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';



function Main() {
    console.log('Main:');

    const userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');

    if (token == '') {
        return MainTokenExeption()
    } else {
        console.log('MainPage:');

        return MainPage()
    }

}


function MainTokenExeption() {
    const navigate = useNavigate();

    function handleClick(event) {
        navigate('/');
    }

    return (
        <div>
            <div>Сеанс истек, требуется заново войти</div>

            <button
                className="button"
                type="button"
                onSubmit={handleClick}

            >Войти</button>

        </div>
    );
}

function MainPage() {
    const [userName, setUserName] = localStorage.getItem('userName');
    const token = localStorage.getItem('token');
    const [rates, setRates] = useState([]);
    const [isOpen, setIsOpen] = React.useState(false);

    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);

    const [showAlert, setShowAlert] = useState(false);

    const handleButtonClick = () => {
        setShowAlert(true);
    };

    const handleCloseClick = () => {
        setShowAlert(false);
    };

    const navigate = useNavigate();

    function handleClick(event) {
        localStorage.clear();
        navigate('/');
        console.log('Токен после удаления:', localStorage.getItem('token'));
    }


    useEffect(() => {
        getRates();
    }, []);


    async function getRates() {
        try {
            const response = await fetch("https://fp-services.ru/installment/courses/get-list", {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${token}`
                }


            });



            if (response.ok) {
                const data = await response.json();

                console.log(data);


                setRates(data.Data);

                setIsOpen(true);

            } else {
                const errorData = await response.json();
                console.error('Ошибка авторизации:', errorData);


            }
        } catch (error) {
            console.error('Ошибка:', error);
        }



    }

    const handleOpen = () => {
        setIsOpen(true);
      };

      const handleClose = () => {
        setIsOpen(false);
      };
    // if (error) {
    //     return (
    //         <div className='r'>
    //             <h1>Ошибка</h1>
    //             <p>Произошла ошибка при загрузке данных.</p>
    //         </div>
    //     );
    // }

    // if (loading) {
    //     return (
    //         <div className='view' >
    //             <h1 className='view_inner'>Ошибка</h1>
    //             <p className='view_title'>Произошла ошибка при загрузке данных.</p>
    //             <button className='button view_button'>Повторить</button>
    //         </div>
    //     )
    // }






    return (
        <div className='main'>
            <table className='table'>
                <thead className='table_thead'>
                    <tr>
                        <th className='table_th'>Name</th>
                        <th className='table_th'>Price</th>
                        <th className='table_th'>Duration</th>
                        <th className='table_th'>Description</th>
                    </tr>
                </thead>
                <tbody className='table_tbody'>
                    {rates.map((rate, index) => (
                        <tr key={index}>
                            <td className='table_td'>{rate.Name}</td>
                            <td className='table_td'>{rate.Price}₽</td>
                            <td className='table_td'>{rate.Duration}</td>
                            <td className='table_td'>{rate.Description}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="button">
                <button type="button" onClick={handleClick}>Выйти</button>
            </div>

            {/* <div>
                {showAlert && (
                    <div className="alert">
                        <h1>Заголовок</h1>
                        <h2>Подзаголовок</h2>
                        <button onClick={handleCloseClick}>Закрыть</button>
                    </div>
                )}
                {!showAlert && (
                    <button onClick={handleButtonClick}>Показать алерт</button>
                )}
            </div> */}

<div>
        <button onClick={handleOpen}>Показать оповещение</button>
        <Modal isOpen={isOpen} onRequestClose={handleClose} className="custom-modal">

          <button onClick={handleClose}>Закрыть</button>
        </Modal>
      </div>
        </div>

    );

}

export default Main;


