    //         <div>userName</div>

    //         <button className="button" type="button" onSubmit={handleClick }>Выйти</button>


    //   </div>
    //   );

    const handleLogout = () => {
        fetch('/logout')
          .then((response) => {
            if (response.ok) {
              // Удалить данные пользователя из localStorage
              localStorage.removeItem('user');
              // Перенаправить на страницу авторизации
              window.location.href = '/';
            }
          })
          .catch((error) => {
            console.error('Ошибка разлогина:', error);
          });
      };

      const LogoutPage = () => {
        useEffect(() => {
          handleLogout();
        }, []);

        return <div>Выход из аккаунта...</div>;
      };




}
