import './index.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import React, { useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';



function Main() {
    console.log('Main:');

    const userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');

    if (token == '') {
        return MainTokenExeption()
    } else {
        console.log('MainPage:');

        return MainPage()
    }

}


function MainTokenExeption() {
    const navigate = useNavigate();

    function handleClick(event) {
        navigate('/');
    }

    return (
        <div>
            <div>Сеанс истек, требуется заново войти</div>

            <button
                className="button"
                type="button"
                onSubmit={handleClick}

            >Войти</button>

        </div>
    );
}

function MainPage() {
    const [userName, setUserName] = useState(localStorage.getItem('userName'));
    const token = localStorage.getItem('token');
    const [rates, setRates] = useState([]);
    const [newRate, setNewRate] = useState({
      name: '',
      price: '',
      duration: '',
      description: ''
    });
    const [isCreating, setIsCreating] = useState(false); // Флаг для отслеживания создания курса

    const navigate = useNavigate();

    function handleClick(event) {
      localStorage.clear();
      navigate('/');
      console.log('Токен после удаления:', localStorage.getItem('token'));
    }

    useEffect(() => {
      getRates();
    }, []);

    async function getRates() {
      try {
        const response = await fetch("https://fp-services.ru/installment/courses/get-list", {
          method: "GET",
          headers: {
            "Authorization": `Bearer ${token}`
          }
        });

        if (response.ok) {
          const data = await response.json();

          console.log(data);

          setRates(data.Data);
        } else {
          const errorData = await response.json();
          console.error('Ошибка авторизации:', errorData);

          setErrorModal({
            isOpen: true,
            errorText: errorData
          });
        }
      } catch (error) {
        console.error('Ошибка:', error);
      }
    }

    async function createRate() {
      try {
        const response = await fetch("https://fp-services.ru/installment/courses/create", {
          method: "POST",
          headers: {
            "Authorization": `Bearer ${token}`,
            "Content-Type": "application/json"
          },
          body: JSON.stringify(newRate) // Отправляем данные нового курса
        });

        if (response.ok) {
          const data = await response.json();

          console.log(data);

          // Обновляем список курсов после успешного создания
          getRates();
        } else {
          const errorData = await response.json();
          console.error('Ошибка создания курса:', errorData);
        }
      } catch (error) {
        console.error('Ошибка:', error);
      }
    }

    function handleCreateClick() {
      setIsCreating(true);
    }

    function handleSaveClick() {
      createRate();
      setIsCreating(false);
      setNewRate({
        name: '',
        price: '',
        duration: '',
        description: ''
      });
    }

    function handleCancelClick() {
      setIsCreating(false);
      setNewRate({
        name: '',
        price: '',
        duration: '',
        description: ''
      });
    }

    function handleInputChange(event) {
      const { name, value } = event.target;
      setNewRate((prevRate) => ({
        ...prevRate,
        [name]: value
      }));
    }

    return (
      <div className='main'>
        <table className='table'>
          <thead className='table_thead'>
            <tr>
              <th className='table_th'>Name</th>
              <th className='table_th'>Price</th>
              <th className='table_th'>Duration</th>
              <th className='table_th'>Description</th>
            </tr>
          </thead>
          <tbody className='table_tbody'>
            {rates.map((rate, index) => (
              <tr key={index}>
                <td className='table_td'>{rate.Name}</td>
                <td className='table_td'>{rate.Price}₽</td>
                <td className='table_td'>{rate.Duration}</td>
                <td className='table_td'>{rate.Description}</td>
              </tr>
            ))}
            {isCreating && (
              <tr>
                <td className='table_td'>
                  <input
                    type="text"
                    name="name"
                    value={newRate.name}
                    onChange={handleInputChange}
                    placeholder="Name"
                  />
                </td>
                <td className='table_td'>
                  <input
                    type="text"
                    name="price"
                    value={newRate.price}
                    onChange={handleInputChange}
                    placeholder="Price"
                  />
                </td>
                <td className='table_td'>
                  <input
                    type="text"
                    name="duration"
                    value={newRate.duration}
                    onChange={handleInputChange}
                    placeholder="Duration"
                  />
                </td>
                <td className='table_td'>
                  <input
                    type="text"
                    name="description"
                    value={newRate.description}
                    onChange={handleInputChange}
                    placeholder="Description"
                  />
                </td>
                <td className='table_td'>
                  <button onClick={handleSaveClick}>Сохранить</button>
                  <button onClick={handleCancelClick}>Отменить</button>
                </td>
              </tr>
            )}
          </tbody>
        </table>
        <div className="button">
          <button type="button" onClick={handleClick}>Выйти</button>
        </div>
      </div>
    );
  }



export default Main;
