import './index.css';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import React, { useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import { Link } from 'react-router-dom';
import Create from './create';
import Back from './Back.png'
import Main from './main';
import { useParams } from 'react-router-dom';






function CurseDetails() {





    var userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');
    var userName = localStorage.getItem('userName');
    const navigate = useNavigate();
    const [rates, setRates] = useState([]);
    const { courseUID } = useParams();
    const [errorModal, setErrorModal] = useState(
        {
            isOpen: false,
            errorText: ''
        });

        const [newCource, setNewCource] = useState({
            Name: '',
            ShortID: uniqueId(),
            Price: 0,
            Duration: '',
            SourceLink: "",
            SourceLink: "",
            CreatedAt: "",
            Description: '',
            detailsButton: true, // флаг указывает на наличие кнопки "Подробнее"
          });

          function uniqueId() {
            const letters = 'abcdefghijklmnopqrstuvwxyz'; // набор букв, из которого будет производиться выбор
            let id = '';
            let length = 5;

            for (let i = 0; i < length; i++) {
              const randomIndex = Math.floor(Math.random() * letters.length);
              const randomLetter = letters.charAt(randomIndex);
              id += randomLetter;
            }

            return id;
          }

    function handleGoBack() {
        navigate('/main'); // Переход на страницу "main"
      }

      function handleClick(event) {
        navigate('/');
    }

    useEffect(() => {
        getRates();
    }, []);

    async function getRates(courseUID) {
        console.log(courseUID);

        try {
            const response = await fetch(`https://fp-services.ru/installment/courses/get?course_uid=${courseUID}`, {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${token}`
                },
                body: JSON.stringify(newCource)
            });

            if (response.ok) {
                const data = await response.json();

                console.log(data);

                  setRates(data.Data);

            } else {
                const errorData = await response.json();
                console.error('CurseDetails error', errorData);

                setErrorModal(
                    {
                        isOpen: true,
                        errorText: errorData
                    }

                )
            }
        } catch (error) {
            console.error('Ошибка:', error);
        }

    }


    async function getCourses() {
     try {
            const response = await fetch(`https://fp-services.ru/installment/courses/get?course_uid=${courseUID}`, {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${token}`
                },
                body: JSON.stringify(newCource)
            });

          if (response.ok) {
            const data = await response.json();
            const courses = data.Data; // Полученный список курсов

            // Обновите состояние с помощью полученного списка курсов
            setCourses(courses);
          } else {
            const errorData = await response.json();
            console.error("Ошибка при получении списка курсов:", errorData);
          }
        } catch (error) {
          console.error("Ошибка при выполнении запроса:", error);
        }
      }


    useEffect(() => {
        getCourses();
      }, []);

    useEffect(() => {
        getRates(courseUID);
      }, [courseUID]);

    return (
        <div className='main'>
             {/* Шапка сайта */}
             <div className='header'>
                <div className='header-title'>
                    <div className='logo'></div>
                    <h3 className='header-text'>SHOOKRU EDUCATION</h3>
                    <button className='button-back' onClick={handleGoBack}> Назад</button>
                </div>

                <div className='header-inner'>
                    <h5 className='Name'>{userName}</h5>
                    <button className='header-button' type="button" onClick={handleClick}>Выйти</button>
                </div>
            </div>

            <table>
  <thead>
    <tr>
      <th>Название</th>
      <th>Цена</th>
      <th>Длительность</th>
      {/* Другие заголовки столбцов */}
    </tr>
  </thead>
  <tbody>
    {courses.map((course) => (
      <tr key={course.CourseUID}>
        <td>{course.Name}</td>
        <td>{course.Price}</td>
        <td>{course.Duration}</td>
        {/* Другие ячейки данных */}
      </tr>
    ))}
  </tbody>
</table>

        </div>
        );
}

export default CurseDetails;
