import './index.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import React, { useEffect } from 'react';
import axios from 'axios';

function Main() {
    console.log('Main:');

    const userName = localStorage.getItem('userName');
    const token = localStorage.getItem('token');

    if (token == '') {
        return MainTokenExeption()
    } else {
        console.log('MainPage:');

        return MainPage()
    }

}


function MainTokenExeption() {
    const navigate = useNavigate();

    function handleClick(event) {
        navigate('/');
    }

    return (
        <div>
            <div>Сеанс истек, требуется заново войти</div>

            <button
                className="button"
                type="button"
                onSubmit={handleClick}

            >Войти</button>

        </div>
    );
}

function MainPage() {
    const [userName, setUserName] = localStorage.getItem('userName');
    const token = localStorage.getItem('token');
    const [rates, setRates] = useState([]);

    const navigate = useNavigate();

    function handleClick(event) {
        localStorage.clear();
        navigate('/');
        console.log('Токен после удаления:', localStorage.getItem('token'));
    }

    function ErrorView() {
        return (
          <div>
            <h1>Ошибка</h1>
            <p>Произошла ошибка при загрузке данных.</p>
          </div>
        );
      }

    useEffect(() => {
        getRates();
    }, []);


    async function getRates() {
        try {
            const response = await fetch("https://fp-services.ru/installment/courses/get-list", {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${token}`
                }


            });

            if (response.ok) {
                const data = await response.json();

                console.log(data);

                setRates(data.Data);
            } else {
                const errorData = await response.json();
                console.error('Ошибка авторизации:', errorData);
                if (error) {
                    return <ErrorView />;
                  }
            }
        } catch (error) {
            console.error('Ошибка:', error);
        }


    }



    return (
        <div className='main'>
            <table className='table'>
                <thead className='table_thead'>
                    <tr>
                        <th  className='table_th'>Name</th>
                        <th  className='table_th'>Price</th>
                        <th className='table_th'>Duration</th>
                        <th className='table_th'>Description</th>
                    </tr>
                </thead>
                <tbody className='table_tbody'>
                    {rates.map((rate, index) => (
                        <tr  key={index}>
                            <td className='table_td'>{rate.Name}</td>
                            <td className='table_td'>{rate.Price}₽</td>
                            <td className='table_td'>{rate.Duration}</td>
                            <td className='table_td'>{rate.Description}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="button">
                <button type="button" onClick={handleClick}>Выйти</button>
            </div>
        </div>

    );

}

export default Main;
