import './index.css';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import React, { useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import {token, getRates} from './main';  
import userName from './Login';




function Create () {
    let token;
    let userName;
    let getRates;

    const [course, setCourse] = useState({
        name: '',
        price: 0,
        duration: '',
        sourceLink: '',
        description: ''
      });

    const navigate = useNavigate();

    const handleNameChange = (e) => {
        setCourse({ ...course, name: e.target.value });
      };

      const handlePriceChange = (e) => {
        setCourse({ ...course, price: e.target.value });
      };

      const handleDurationChange = (e) => {
        setCourse({ ...course, duration: e.target.value });
      };

      const handleSourceLinkChange = (e) => {
        setCourse({ ...course, sourceLink: e.target.value });
      };

      const handleDescriptionChange = (e) => {
        setCourse({ ...course, description: e.target.value });
      };

      // Обработчик отправки формы
      const handleSubmit = (e) => {
        e.preventDefault();
        // Отправка запроса для создания курса
        // ...
      };


    return (
        <div>
                  <div className='header'>
                <div className='header-title'>
                <div className='logo'>Логотип</div>
                <h3 className='header-text'>SHOOKRU EDUCATION</h3>
                </div>

                <div className='header-inner'>
                <h5 className='Name'>{userName}</h5>
                {/* <button className='header-button' type="button" onClick={handleClick}>Выйти</button> */}
                </div>
            </div>
            <div className="create">
            {/* {!isCreating && ( */}
              <button type="create-button">Создать</button>
              {/* <button type="create-button" onClick={handleCreateClick}>Создать</button> */}
            {/* )} */}


          </div>
<div>
          <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" value={course.name} onChange={handleNameChange} />
        </label>
        <br />
        <label>
          Price:
          <input type="number" value={course.price} onChange={handlePriceChange} />
        </label>
        <br />
        <label>
          Duration:
          <input type="text" value={course.duration} onChange={handleDurationChange} />
        </label>
        <br />
        <label>
          Source Link:
          <input type="text" value={course.sourceLink} onChange={handleSourceLinkChange} />
        </label>
        <br />
        <label>
          Description:
          <textarea value={course.description} onChange={handleDescriptionChange} />
        </label>
        <br />
        <button type="submit">Create</button>
      </form>

      </div>
        </div>

    );

}


export default Create;
